targets.expr:
pql step=1h time()  - kube_pod_start_time{namespace="$namespace",pod=~"(ds-|m1-)*($application).*", env="$env"} 
| rename value as pod_age
| fields pod, pod_age

pql step=1h time() - container_start_time_seconds{namespace="$namespace",pod=~"(ds-|m1-)*($application).*", env="$env",container_name!=""} 
| stats min(value) as uptime by pod 
| fields pod, uptime
